import React from 'react';
import { Link } from 'react-router-dom';
import { useCart } from '../context/CartContext';

function ProductCard({ product }) {
  const { dispatch } = useCart();

  const handleAddToCart = () => {
    dispatch({ 
        type: 'ADD_ITEM', 
        payload: { 
            id: product.id, 
            title: product.title, 
            price: parseFloat(product.price), 
            quantity: 1 
        } 
    });
    alert(`‚ú® ${product.title} added to cart! üõçÔ∏è`);
  };

  const isLowStock = product.inventory_count <= 10;
  const isExpensive = parseFloat(product.price) >= 2000;

  return (
    <div className="border rounded-xl overflow-hidden shadow-lg hover:shadow-2xl transition-all duration-300 bg-white transform hover:-translate-y-1">
      <div className="relative">
        <Link to={`/products/${product.id}`}>
          <img 
            src={product.image_url || 'https://via.placeholder.com/400?text=GemCart+Product'} 
            alt={product.title} 
            className="w-full h-64 object-cover hover:scale-105 transition-transform duration-300" 
          />
        </Link>
        {isExpensive && (
          <div className="absolute top-2 left-2 bg-gradient-to-r from-yellow-400 to-yellow-600 text-white px-2 py-1 rounded-full text-xs font-bold">
            üëë LUXURY
          </div>
        )}
        {isLowStock && (
          <div className="absolute top-2 right-2 bg-red-500 text-white px-2 py-1 rounded-full text-xs font-bold">
            ‚ö° LOW STOCK
          </div>
        )}
      </div>
      <div className="p-4">
        <div className="flex items-center gap-1 text-xs text-gray-500 mb-2">
          {product.categories.map(cat => (
            <span key={cat.id} className="bg-gray-100 px-2 py-1 rounded-full">{cat.name}</span>
          ))}
        </div>
        <Link to={`/products/${product.id}`}>
          <h3 className="text-lg font-semibold text-gray-900 hover:text-teal-600 transition-colors line-clamp-2">{product.title}</h3>
        </Link>
        <div className="flex items-center justify-between mt-2">
          <p className="text-2xl font-bold text-gray-800">üí∞ ${parseFloat(product.price).toFixed(2)}</p>
          <span className="text-sm text-gray-500">üì¶ {product.inventory_count} left</span>
        </div>
        <button
          onClick={handleAddToCart}
          disabled={product.inventory_count === 0}
          className="mt-3 w-full bg-gradient-to-r from-teal-600 to-teal-700 text-white py-2 rounded-lg hover:from-teal-700 hover:to-teal-800 transition-all duration-200 font-semibold disabled:bg-gray-400 disabled:cursor-not-allowed"
        >
          {product.inventory_count > 0 ? 'üõçÔ∏è Add to Cart' : '‚ùå Out of Stock'}
        </button>
      </div>
    </div>
  );
}

export default ProductCard;
