import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import ProductCard from '../components/ProductCard';

function Products() {
    const [products, setProducts] = useState([]);
    const [categories, setCategories] = useState([]);
    const [loading, setLoading] = useState(true);
    const location = useLocation();
    const urlParams = new URLSearchParams(location.search);
    const [filters, setFilters] = useState({
        search: '',
        category: urlParams.get('category') || '',
        sort: '',
        minPrice: '',
        maxPrice: ''
    });

    useEffect(() => {
        const urlCategory = urlParams.get('category');
        if (urlCategory && urlCategory !== filters.category) {
            setFilters(prev => ({ ...prev, category: urlCategory }));
        }
    }, [location.search]);

    useEffect(() => {
        fetchProducts();
        fetchCategories();
    }, [filters]);

    const fetchProducts = () => {
        const params = new URLSearchParams();
        Object.entries(filters).forEach(([key, value]) => {
            if (value) params.append(key === 'minPrice' ? 'min_price' : key === 'maxPrice' ? 'max_price' : key, value);
        });

        fetch(`/api/products?${params}`)
            .then(res => res.json())
            .then(data => {
                setProducts(data);
                setLoading(false);
            })
            .catch(err => {
                console.error(err);
                setLoading(false);
            });
    };

    const fetchCategories = () => {
        fetch('/api/products/categories')
            .then(res => res.json())
            .then(setCategories)
            .catch(console.error);
    };

    const handleFilterChange = (key, value) => {
        setFilters(prev => ({ ...prev, [key]: value }));
        setLoading(true);
    };

    if (loading) return <div className="text-center py-10">✨ Loading Luxury Collection...</div>;

    return (
        <div className="container mx-auto p-4">
            <div className="text-center mb-8">
                <h1 className="text-4xl font-bold mb-4">💎 Luxury Jewelry & Watches</h1>
                <p className="text-gray-600">🌟 Discover Exquisite Pieces for Every Occasion</p>
            </div>

            {/* Filters */}
            <div className="bg-white p-6 rounded-lg shadow-md mb-8">
                <div className="grid grid-cols-1 md:grid-cols-5 gap-4">
                    <input
                        type="text"
                        placeholder="🔍 Search jewelry..."
                        value={filters.search}
                        onChange={(e) => handleFilterChange('search', e.target.value)}
                        className="p-3 border rounded-lg focus:ring-2 focus:ring-teal-500"
                    />

                    <select
                        value={filters.category}
                        onChange={(e) => handleFilterChange('category', e.target.value)}
                        className="p-3 border rounded-lg focus:ring-2 focus:ring-teal-500"
                    >
                        <option value="">🏷️ All Categories</option>
                        {categories.map(cat => (
                            <option key={cat.id} value={cat.name}>{cat.name}</option>
                        ))}
                    </select>

                    <input
                        type="number"
                        placeholder="💰 Min Price"
                        value={filters.minPrice}
                        onChange={(e) => handleFilterChange('minPrice', e.target.value)}
                        className="p-3 border rounded-lg focus:ring-2 focus:ring-teal-500"
                    />

                    <input
                        type="number"
                        placeholder="💎 Max Price"
                        value={filters.maxPrice}
                        onChange={(e) => handleFilterChange('maxPrice', e.target.value)}
                        className="p-3 border rounded-lg focus:ring-2 focus:ring-teal-500"
                    />

                    <select
                        value={filters.sort}
                        onChange={(e) => handleFilterChange('sort', e.target.value)}
                        className="p-3 border rounded-lg focus:ring-2 focus:ring-teal-500"
                    >
                        <option value="">📊 Sort By</option>
                        <option value="price_asc">💲 Price: Low to High</option>
                        <option value="price_desc">💰 Price: High to Low</option>
                    </select>
                </div>
            </div>

            <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
                {products.map(product => (
                    <ProductCard key={product.id} product={product} />
                ))}
            </div>

            {products.length === 0 && (
                <div className="text-center mt-10">
                    <p className="text-xl text-gray-500">😔 No products found</p>
                    <p className="text-gray-400">Try adjusting your filters</p>
                </div>
            )}
        </div>
    );
}

export default Products;
