import React from 'react';
import { Link } from 'react-router-dom';
import { useCart } from '../context/CartContext';

function ProductCard({ product }) {
  const { dispatch } = useCart();
  const handleAddToCart = () => {
    dispatch({ 
        type: 'ADD_ITEM', 
        payload: { 
            id: product.id, 
            title: product.title, 
            price: parseFloat(product.price), 
            quantity: 1 
        } 
    });
    alert(`‚ú® ${product.title} added to cart! üõçÔ∏è`);
  };

  const isLowStock = product.inventory_count <= 10;
  const isExpensive = parseFloat(product.price) >= 2000;
  const getJewelryImage = (category, title) => {
    const images = {
      'Rings': 'https://images.unsplash.com/photo-1605100804763-247f67b3557e?w=400&h=400&fit=crop',
      'Necklaces': 'https://images.unsplash.com/photo-1515562141207-7a88fb7ce338?w=400&h=400&fit=crop',
      'Watches': 'https://images.unsplash.com/photo-1524592094714-0f0654e20314?w=400&h=400&fit=crop',
      'Earrings': 'https://images.unsplash.com/photo-1535632066927-ab7c9ab60908?w=400&h=400&fit=crop',
      'Chains': 'https://images.unsplash.com/photo-1611652022419-a9419f74343d?w=400&h=400&fit=crop',
      'Gems': 'https://images.unsplash.com/photo-1506905925346-21bda4d32df4?w=400&h=400&fit=crop',
      'Bracelets': 'https://images.unsplash.com/photo-1611591437281-460bfbe1220a?w=400&h=400&fit=crop'
    };
    const categoryName = product.categories?.[0]?.name || 'Rings';
    return product.image_url || images[categoryName] || images['Rings'];
  };

  return (
    <div className="jewelry-card group relative bg-white rounded-2xl shadow-lg hover:shadow-2xl transition-all duration-500 overflow-hidden border border-gray-100">
      <div className="relative overflow-hidden">
        <Link to={`/products/${product.id}`}>
          <img 
            src={getJewelryImage()} 
            alt={product.title} 
            className="w-full h-72 object-cover group-hover:scale-110 transition-transform duration-700" 
          />
        </Link>
        <div className="absolute inset-0 bg-gradient-to-t from-black/20 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-300"></div>

        {/* Badges */}
        <div className="absolute top-3 left-3 flex flex-col gap-2">
          {isExpensive && (
            <span className="bg-gradient-to-r from-yellow-400 to-yellow-600 text-white px-3 py-1 rounded-full text-xs font-bold shadow-lg">
              üëë LUXURY
            </span>
          )}
          {isLowStock && (
            <span className="bg-red-500 text-white px-3 py-1 rounded-full text-xs font-bold shadow-lg animate-pulse">
              ‚ö° ONLY {product.inventory_count} LEFT
            </span>
          )}
        </div>


      </div>

      <div className="p-5">


        {/* Title */}
        <Link to={`/products/${product.id}`}>
          <h3 className="text-lg font-serif font-bold text-gray-900 hover:text-yellow-600 transition-colors mb-2 line-clamp-2 group-hover:text-yellow-600">
            {product.title}
          </h3>
        </Link>



        {/* Price and Stock */}
        <div className="flex items-center justify-between mb-4">
          <div>
            <p className="text-2xl font-bold text-gray-900">${parseFloat(product.price).toFixed(2)}</p>
            <p className="text-sm text-gray-500">Free shipping worldwide</p>
          </div>
          <div className="text-right">
            <span className="text-sm text-gray-500">In stock: {product.inventory_count}</span>
          </div>
        </div>

        {/* Add to Cart Button */}
        <button
          onClick={handleAddToCart}
          disabled={product.inventory_count === 0}
          className="w-full bg-gradient-to-r from-yellow-500 to-yellow-600 text-black py-3 rounded-xl hover:from-yellow-600 hover:to-yellow-700 transition-all duration-300 font-serif font-semibold disabled:bg-gray-400 disabled:cursor-not-allowed transform hover:scale-105 shadow-lg"
        >
          {product.inventory_count > 0 ? 'üõçÔ∏è Add to Cart' : '‚ùå Out of Stock'}
        </button>
      </div>
    </div>
  );
}

export default ProductCard;
