import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import { AuthProvider, useAuth } from './context/AuthContext';
import { CartProvider, useCart } from './context/CartContext';
import Products from './pages/Products';
import ProductDetail from './pages/ProductDetail';
import Login from './pages/Login';
import Cart from './pages/Cart';
import Checkout from './pages/Checkout';
import Home from './pages/Home';

const Navbar = () => {
  const { state, dispatch } = useAuth();
  const { state: cartState, cartTotal } = useCart();

  const handleLogout = () => {
    dispatch({ type: 'LOGOUT' });
  };

  return (
    <header className="bg-gradient-to-r from-gray-900 to-gray-800 text-white p-4 shadow-lg">
      <div className="container mx-auto flex justify-between items-center">
        <Link to="/" className="text-2xl font-bold hover:text-teal-300 transition-colors">
          üíé GemCart
        </Link>
        <nav className="space-x-6 flex items-center">
          <Link to="/products" className="hover:text-teal-300 transition-colors font-medium">
            üõçÔ∏è Shop
          </Link>
          <Link to="/cart" className="relative">
            <span className="bg-gradient-to-r from-teal-600 to-teal-700 px-3 py-2 rounded-full text-sm font-bold hover:from-teal-700 hover:to-teal-800 transition-all cursor-pointer">
              üõçÔ∏è Cart ({cartState.items.length}) - ${cartTotal}
            </span>
            {cartState.items.length > 0 && (
              <span className="absolute -top-2 -right-2 bg-red-500 text-white rounded-full w-5 h-5 flex items-center justify-center text-xs font-bold animate-pulse">
                {cartState.items.length}
              </span>
            )}
          </Link>
          {state.isAuthenticated ? (
            <>
              <span className="text-teal-300">üë§ Hello, {state.user.username}</span>
              {state.user.is_seller && (
                <span className="bg-yellow-600 px-2 py-1 rounded-full text-xs font-bold">
                  üëë SELLER
                </span>
              )}
              <button onClick={handleLogout} className="hover:text-red-300 transition-colors">
                üö™ Logout
              </button>
            </>
          ) : (
            <Link to="/login" className="bg-teal-600 hover:bg-teal-700 px-4 py-2 rounded-lg transition-colors font-medium">
              üîë Login
            </Link>
          )}
        </nav>
      </div>
    </header>
  );
};



function App() {
  return (
    <Router>
      <AuthProvider>
        <CartProvider>
          <Navbar />
          <main>
            <Routes>
              <Route path="/" element={<Home />} />
              <Route path="/products" element={<Products />} />
              <Route path="/products/:id" element={<ProductDetail />} />
              <Route path="/cart" element={<Cart />} />
              <Route path="/checkout" element={<Checkout />} />
              <Route path="/login" element={<Login />} />
            </Routes>
          </main>
        </CartProvider>
      </AuthProvider>
    </Router>
  );
}

export default App;
