import React from 'react';
import { Link } from 'react-router-dom';
import { useCart } from '../context/CartContext';

function ProductCard({ product }) {
  const { dispatch } = useCart();

  const handleAddToCart = () => {
    dispatch({ 
        type: 'ADD_ITEM', 
        payload: { 
            id: product.id, 
            title: product.title, 
            price: parseFloat(product.price), 
            quantity: 1 
        } 
    });
    alert(`${product.title} added to cart!`);
  };

  return (
    <div className="border rounded-lg overflow-hidden shadow-lg hover:shadow-xl transition duration-300 bg-white">
      <Link to={`/products/${product.id}`}>
        <img 
          src={product.image_url || 'https://via.placeholder.com/400?text=GemCart+Product'} 
          alt={product.title} 
          className="w-full h-64 object-cover" 
        />
      </Link>
      <div className="p-4">
        <Link to={`/products/${product.id}`}>
          <h3 className="text-lg font-semibold text-gray-900 truncate">{product.title}</h3>
        </Link>
        <p className="text-2xl font-bold text-gray-800 mt-1">${parseFloat(product.price).toFixed(2)}</p>
        <button
          onClick={handleAddToCart}
          className="mt-3 w-full bg-teal-600 text-white py-2 rounded-lg hover:bg-teal-700 transition duration-150"
        >
          Add to Cart
        </button>
      </div>
    </div>
  );
}

export default ProductCard;
