import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { useCart } from '../context/CartContext';

function ProductDetail() {
  const { id } = useParams();
  const { dispatch } = useCart();
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [quantity, setQuantity] = useState(1);

  useEffect(() => {
    fetch(`/api/products/${id}`)
      .then(res => res.json())
      .then(data => {
        setProduct(data);
        setLoading(false);
      })
      .catch(err => {
        console.error(err);
        setLoading(false);
      });
  }, [id]);

  const handleAddToCart = () => {
    if (product) {
      dispatch({ 
        type: 'ADD_ITEM', 
        payload: { 
          id: product.id, 
          title: product.title, 
          price: parseFloat(product.price), 
          quantity: quantity 
        } 
      });
      alert(`${quantity} x ${product.title} added to cart!`);
    }
  };

  if (loading) return <div className="text-center py-10">Loading product details...</div>;
  if (!product) return <div className="text-center py-10">Product not found.</div>;

  return (
    <div className="container mx-auto p-8">
      <div className="flex flex-col lg:flex-row gap-10">
        <div className="lg:w-1/2">
          <img 
            src={product.image_url || 'https://via.placeholder.com/800x600?text=GemCart+Image'} 
            alt={product.title} 
            className="w-full h-auto object-cover rounded-lg shadow-xl"
          />
        </div>

        <div className="lg:w-1/2 space-y-6">
          <h1 className="text-4xl font-extrabold text-gray-900">{product.title}</h1>

          <div className="text-3xl font-bold text-teal-600">${parseFloat(product.price).toFixed(2)} USD</div>

          <p className="text-gray-700 text-lg">{product.description}</p>

          <div className="border-t pt-4 space-y-4">
            <div className="flex items-center space-x-4">
                <label htmlFor="quantity" className="font-semibold">Quantity:</label>
                <input 
                    type="number" 
                    id="quantity"
                    min="1"
                    max={product.inventory_count}
                    value={quantity}
                    onChange={(e) => setQuantity(parseInt(e.target.value))}
                    className="w-20 p-2 border rounded-md text-center"
                />
            </div>

            <button
                onClick={handleAddToCart}
                disabled={product.inventory_count <= 0}
                className="w-full bg-blue-600 text-white font-bold py-3 px-6 rounded-lg text-lg hover:bg-blue-700 transition duration-200 disabled:bg-gray-400"
            >
                {product.inventory_count > 0 ? "Add to Cart" : "Out of Stock"}
            </button>
            <div className="text-sm text-gray-500">
                In Stock: {product.inventory_count} units
            </div>
          </div>

          <div className="mt-8 border-t pt-4">
            <h3 className="text-xl font-semibold">Product Details</h3>
            <ul className="text-gray-600 mt-2">
                <li><strong>SKU:</strong> {product.sku}</li>
                <li><strong>Category:</strong> {product.categories.map(c => c.name).join(', ')}</li>
                <li><strong>Seller:</strong> {product.seller.username}</li>
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
}

export default ProductDetail;
