import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import { AuthProvider, useAuth } from './context/AuthContext';
import { CartProvider, useCart } from './context/CartContext';
import Products from './pages/Products';
import ProductDetail from './pages/ProductDetail';
import Login from './pages/Login';

const Navbar = () => {
  const { state, dispatch } = useAuth();
  const { state: cartState } = useCart();

  const handleLogout = () => {
    dispatch({ type: 'LOGOUT' });
  };

  return (
    <header className="bg-gray-800 text-white p-4">
      <div className="container mx-auto flex justify-between items-center">
        <Link to="/" className="text-xl font-bold">ðŸ’Ž GemCart</Link>
        <nav className="space-x-4 flex items-center">
          <Link to="/products" className="hover:text-gray-300">Shop</Link>
          <span className="bg-teal-600 px-2 py-1 rounded text-sm">
            Cart ({cartState.items.length})
          </span>
          {state.isAuthenticated ? (
            <>
              <span>Hello, {state.user.username}</span>
              <button onClick={handleLogout} className="hover:text-gray-300">Logout</button>
            </>
          ) : (
            <Link to="/login" className="hover:text-gray-300">Login</Link>
          )}
        </nav>
      </div>
    </header>
  );
};

const Home = () => (
  <div className="container mx-auto p-8 text-center">
    <h1 className="text-4xl font-bold mb-4">Welcome to GemCart</h1>
    <p className="text-xl mb-8">Discover exquisite jewelry and luxury watches</p>
    <Link 
      to="/products" 
      className="bg-teal-600 text-white px-6 py-3 rounded-lg text-lg hover:bg-teal-700 transition"
    >
      Shop Now
    </Link>
  </div>
);

function App() {
  return (
    <Router>
      <AuthProvider>
        <CartProvider>
          <Navbar />
          <main>
            <Routes>
              <Route path="/" element={<Home />} />
              <Route path="/products" element={<Products />} />
              <Route path="/products/:id" element={<ProductDetail />} />
              <Route path="/login" element={<Login />} />
            </Routes>
          </main>
        </CartProvider>
      </AuthProvider>
    </Router>
  );
}

export default App;
